SELECT m.rid, m.parent FROM crrs_group_members m, (
  SELECT member_code, COUNT(*) c FROM crrs_group_members t
  WHERE parent = '1' AND EXISTS (
    SELECT * FROM crrs_group_group_client WHERE credit_type = '1' AND customer_code = t.customer_code AND data_dt = '${DATA_DT}'
  ) GROUP BY member_code
) t WHERE m.member_code = t.member_code AND t.c > 1 AND parent = '1' AND EXISTS (
  SELECT * FROM crrs_group_group_client WHERE credit_type = '1' AND customer_code = m.customer_code AND data_dt = '${DATA_DT}'
)

SELECT rid, enterprise_code FROM crrs_ent_bond t
WHERE NOT EXISTS (
  SELECT * FROM crrs_ent_credit WHERE customer_code = t.enterprise_code AND customer_type = '2'
) AND NOT EXISTS (
  SELECT * FROM crrs_ent_group_client WHERE customer_client_code = t.enterprise_code
) AND EXISTS (SELECT * FROM crrs_single_corporation WHERE customer_code = t.customer_code AND data_dt = '${DATA_DT}')

SELECT rid, customer_code FROM crrs_ent_offbalance_sa t
WHERE NOT EXISTS (
  SELECT * FROM crrs_ent_credit WHERE customer_code = t.customer_code AND customer_type = '2'
) AND NOT EXISTS (
  SELECT * FROM crrs_ent_group_client WHERE customer_client_code = t.customer_code
) AND EXISTS (SELECT * FROM crrs_single_corporation WHERE customer_code = t.customer_code AND data_dt = '${DATA_DT}')

SELECT c.rid, c.org_code_no_scene FROM crrs_ent_trade_info c, (
  SELECT * FROM (
    SELECT org_code_no_scene, COUNT(*) c
    FROM crrs_ent_trade_info WHERE data_dt = '${DATA_DT}' GROUP BY org_code_no_scene
  ) WHERE c > 1
) t WHERE c.org_code_no_scene = t.org_code_no_scene AND c.data_dt = '${DATA_DT}'

SELECT t.rid, '' FROM crrs_single_corporation t
WHERE t.customer_class IN ('A1', 'A2') AND EXISTS ( 
  SELECT * FROM crrs_ent_credit WHERE customer_code = t.customer_code AND credit_code<>'WNRSXYW'
) AND EXISTS (
  SELECT * FROM crrs_ent_loan WHERE customer_code = t.customer_code AND loan_type IN ('1', '3', '5')
) AND general_assets IS NULL AND data_dt='${DATA_DT}'

SELECT t.rid, '' FROM crrs_single_corporation t
WHERE t.customer_class IN ('A1', 'A2') AND EXISTS (
  SELECT * FROM crrs_ent_credit WHERE customer_code = t.customer_code AND credit_code<>'WNRSXYW'
) AND EXISTS (
  SELECT * FROM crrs_ent_loan p WHERE p.customer_code = t.customer_code AND p.loan_type IN ('1', '3', '5')
) AND business_income IS NULL AND data_dt='${DATA_DT}'

SELECT c.rid, c.customer_code FROM crrs_single_corporation c, (
  SELECT * FROM (
    SELECT customer_code, COUNT(*) c
    FROM crrs_single_corporation  WHERE data_dt = '${DATA_DT}' GROUP BY customer_code
  ) WHERE c > 1
) t WHERE c.customer_code = t.customer_code

SELECT rid, customer_code FROM crrs_ent_guaranteed t
WHERE NOT EXISTS (
  SELECT * FROM crrs_ent_loan WHERE customer_code = t.customer_code
) AND NOT EXISTS (
  SELECT * FROM crrs_ent_offbalance_sa WHERE customer_code = t.customer_code
) AND EXISTS (SELECT * FROM crrs_single_corporation WHERE customer_code = t.customer_code AND data_dt = '${DATA_DT}')

SELECT t.rid, t.customer_code FROM crrs_single_corporation t
WHERE (EXISTS (
  SELECT * FROM crrs_ent_credit WHERE customer_code = t.customer_code AND credit_code <> 'WNRSXYW'
) OR EXISTS (
  SELECT * FROM crrs_ent_loan WHERE customer_code = t.customer_code
) OR EXISTS (
  SELECT * FROM crrs_ent_equitystake WHERE customer_code = t.customer_code
) OR EXISTS (
  SELECT * FROM crrs_ent_offbalance_sa WHERE customer_code = t.customer_code
) OR EXISTS (
  SELECT * FROM crrs_ent_trade_customers WHERE customer_code = t.customer_code
)) AND NOT EXISTS (
  SELECT * FROM crrs_single_executives WHERE customer_code = t.customer_code
) AND data_dt='${DATA_DT}'

