SELECT c.rid, c.customer_code FROM crrs_ent_trade_info c, (
  SELECT * FROM (
    SELECT customer_code, COUNT(*) c
    FROM crrs_ent_trade_info WHERE data_dt = '${DATA_DT}' GROUP BY customer_code
  ) WHERE c > 1
) t WHERE c.customer_code = t.customer_code

