SELECT c.rid, c.paper_code FROM crrs_person_joint_debtor c, (
  SELECT * FROM (
    SELECT p.evidenceofdebt_number, name, t.paper_type, t.paper_code, COUNT(*) c
    FROM crrs_person_joint_debtor t, crrs_person_personal_loan p
    WHERE p.loan_contract_number = t.loan_contract_number AND EXISTS (
      SELECT * FROM crrs_person_personal WHERE customer_code = t.customer_code AND data_dt = '${DATA_DT}'
    ) GROUP BY evidenceofdebt_number, name, paper_type, paper_code
  ) WHERE c > 1
) t WHERE c.name = t.name AND c.paper_type = t.paper_type AND c.paper_code = t.paper_code

SELECT rid, paper_code FROM crrs_single_shareholder_ep t
WHERE enterprise_type = '6' AND NOT EXISTS (
  SELECT rid FROM crrs_single_executives WHERE id_card_no = t.paper_code
) AND NOT EXISTS (
  SELECT p.rid FROM crrs_single_executives e, crrs_passport p WHERE e.customer_code = t.customer_code AND p.executives_id = e.executives_id AND p.passport_number = t.paper_code
) AND NOT EXISTS (
  SELECT p.rid FROM crrs_single_executives e, crrs_paper p WHERE e.customer_code = t.customer_code AND p.executives_id = e.executives_id AND p.paper_code = t.paper_code
) AND EXISTS (SELECT * FROM crrs_single_corporation WHERE customer_code = t.customer_code AND data_dt = '${DATA_DT}')

SELECT rid, loancontract_number FROM crrs_ent_guaranteed t
WHERE NOT EXISTS (
  SELECT * FROM crrs_ent_loan WHERE loan_contract_number = t.loancontract_number
) AND NOT EXISTS (
  SELECT * FROM crrs_ent_offbalance_sa WHERE contract_number = t.loancontract_number
) AND EXISTS (SELECT * FROM crrs_single_corporation WHERE customer_code = t.customer_code AND data_dt = '${DATA_DT}')

