SELECT c.rid, c.business_number FROM crrs_ent_offbalance_sa c, (
  SELECT * FROM (
    SELECT business_type, business_number, COUNT(*) c
    FROM crrs_ent_offbalance_sa p
    WHERE EXISTS (SELECT * FROM crrs_single_corporation WHERE customer_code = p.customer_code AND data_dt = '${DATA_DT}')
    GROUP BY business_type, business_number
  ) WHERE c > 1
) t WHERE c.business_type = t.business_type AND c.business_number = t.business_number AND EXISTS (
  SELECT * FROM crrs_single_corporation WHERE customer_code = c.customer_code AND data_dt = '${DATA_DT}'
)
